import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserPage.css'; // Tegyük fel, hogy már létrehoztad ezt a stíluslapot

const UserPage = ({ user, onLogout, onUpdateProfile, onFavouriteToggle, onBookReserve }) => {
  const [profileImage, setProfileImage] = useState(user.profileImage || '');
  const [favouriteBooks, setFavouriteBooks] = useState(user.favouriteBooks || []);
  const navigate = useNavigate();

  useEffect(() => {
    // Itt ellenőrizheted, hogy a felhasználó be van-e jelentkezve
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleImageChange = (e) => {
    const newImage = e.target.files[0];
    // Itt feltöltheted az új képet (például egy API segítségével)
    onUpdateProfile({...user, profileImage: newImage});
  };

  const toggleFavourite = (bookId) => {
    const isFavourite = favouriteBooks.includes(bookId);
    const updatedFavourites = isFavourite
      ? favouriteBooks.filter(id => id !== bookId)
      : [...favouriteBooks, bookId];
    
    setFavouriteBooks(updatedFavourites);
    onFavouriteToggle(updatedFavourites); // Frissíted a backenden is az új kedvencek listáját
  };

  const reserveBook = (bookId) => {
    onBookReserve(bookId); // Itt jelezheted az adminnak, hogy a könyv lefoglalásra került
  };

  return (
    <div className="user-page">
      <h1>Felhasználói Profil</h1>
      <button onClick={onLogout}>Kijelentkezés</button>
      <div>
        <label>Profilkép: </label>
        <input type="file" onChange={handleImageChange} />
        {profileImage && <img src={profileImage} alt="Profilkép" />}
      </div>
      <div>
        <h2>Kedvenc könyveim</h2>
        {favouriteBooks.map(bookId => (
          <div key={bookId}>
            {/* Itt jelenítheted meg a kedvenc könyveket, például egy cím és egy "Eltávolítás a kedvencek közül" gombbal */}
            <button onClick={() => toggleFavourite(bookId)}>Eltávolítás</button>
          </div>
        ))}
      </div>
      {/* Itt lehetőséget biztosíthatsz további könyvek kedvencnek jelölésére vagy lefoglalására */}
    </div>
  );
};

export default UserPage;
