import React, { useState } from 'react';
import './Books.css';
import Modal from './Modal';
import Pagination from './Pagination';

const Books = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10); // Fix értékű, így nem kell állítani


  

  const books = [
    { id: '1', title: 'Naruto Shippuden', description: 'Egy ninja története...' },
    { id: '2', title: 'Tokyo Ghoul', description: 'Egy fiatal fiú, aki ghoul-lá válik...' },
    { id: '3', title: 'Csokonai Vitéz Mihály' },
    { id: '4', title: 'Sebzett madár' },
    { id: '5', title: 'Toldi-trilógia' },
    { id: '6', title: 'Jónás Könyve' },
    { id: '7', title: 'A vegyész' },
    { id: '8', title: 'Összes Versei' },
    { id: '9', title: 'Goriot apó' },
    { id: '10', title: 'Stanley kincse' },
    { id: '11', title: 'Arany János válogatott versei' },
    { id: '12', title: 'Az utolsó boszorkány' },
    { id: '13', title: 'Veszélyes Gazdaság' },
    { id: '14', title: 'Agykontroll' },
    { id: '15', title: 'Szótár' },
    { id: '16', title: 'Görög drámák' },
    { id: '17', title: 'Huhogók' },
    { id: '18', title: 'Az elvarázsolt lélek' },
    { id: '19', title: 'Éjjeli Őrjárat' },
    { id: '20', title: 'Tizenkilencen' },
    // További könyvek...

  ];

  const handleInfoClick = (bookId) => {
    const book = books.find(book => book.id === bookId);
    setSelectedBook(book);
    document.body.style.overflow = 'hidden'; // Letiltja a háttér görgetését
  };

  const handleCloseModal = () => {
    setSelectedBook(null);
    document.body.style.overflow = 'auto'; // Visszaállítja a görgetést
  };

  // Számítás az aktuális oldalon megjelenítendő könyvekről
  const indexOfLastBook = currentPage * itemsPerPage;
  const indexOfFirstBook = indexOfLastBook - itemsPerPage;
  const currentBooks = books.slice(indexOfFirstBook, indexOfLastBook);

  const paginate = pageNumber => setCurrentPage(pageNumber);


  return (
    <main className="page-content">
    {currentBooks.map((book) => (
      <div key={book.id} className="card">
        <div className="content">
          <h2 className="title">{book.title}</h2>
          <p className="copy">{book.description || "Nincs elérhető leírás."}</p>
          <button className="btn" onClick={() => handleInfoClick(book.id)}>Info</button>
        </div>
      </div>
      ))}
      <Pagination
        itemsPerPage={itemsPerPage}
        totalItems={books.length}
        paginate={paginate}
      />
      {selectedBook && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}> {/* Megakadályozza a buborékolást */}
            <h2>{selectedBook.title}</h2>
            <p>{selectedBook.description}</p>
            <button className="btn" onClick={handleCloseModal}>Close</button>
          </div>
          
        </div>
      )}
          {selectedBook && <Modal book={selectedBook} onClose={handleCloseModal} />}

    </main>
  );
};

export default Books;
