import React, { useState, useEffect } from 'react';
import './Books.css';
import Modal from './Modal';
import Pagination from './Pagination';

const Books = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(8); // Fix értékű, így nem kell állítani
  const [searchTerm, setSearchTerm] = useState(''); // A `searchTerm` állapot helyes inicializálása
  const [filteredBooks, setFilteredBooks] = useState([]);

  const books = [
    
  ];

  useEffect(() => {
    const searchResults = searchTerm
      ? books.filter(book =>
          book.title.toLowerCase().includes(searchTerm.toLowerCase())
        )
      : books;
    setFilteredBooks(searchResults);
  }, [searchTerm, books]);

  const handleInfoClick = bookId => {
    const book = filteredBooks.find(book => book.id === bookId);
    setSelectedBook(book);
    document.body.style.overflow = 'hidden'; // Letiltja a háttér görgetését
  };

  const handleCloseModal = () => {
    setSelectedBook(null);
    document.body.style.overflow = 'auto'; // Visszaállítja a görgetést
  };

  // Az aktuális oldalon megjelenítendő könyvek kiszámítása
  const indexOfLastBook = currentPage * itemsPerPage;
  const indexOfFirstBook = indexOfLastBook - itemsPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <main className="page-content">
      {currentBooks.map(book => (
        <div key={book.id} className="card">
          <div className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy">{book.description || "Nincs elérhető leírás."}</p>
            <button className="btn" onClick={() => handleInfoClick(book.id)}>Info</button>
          </div>
        </div>
      ))}
      <Pagination
        itemsPerPage={itemsPerPage}
        totalItems={filteredBooks.length}
        paginate={paginate}
      />
      {selectedBook && <Modal book={selectedBook} onClose={handleCloseModal} />}
    </main>
  );
};

export default Books;
