import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './Books.css';

const Books = () => {
  const initialBooks = [
    { id: 'Naruto Shippuden', title: 'Naruto Shippuden', description: 'Egy ninja története...' },
    { id: 'Tokyo Ghoul', title: 'Tokyo Ghoul', description: 'Egy fiatal fiú, aki ghoul-lá válik...' },
    { id: 'Dorottya', title: 'Csokonai Vitéz Mihály',description:'' },
    { id: 'Sebzett madár', title: 'Sebzett madár' ,description:'' },
    { id: 'Toldi-trilógia', title: 'Toldi-trilógia',description:'' },
    { id: 'Jónás Könyve', title: 'Jónás Könyve' ,description:''},
    { id: 'A vegyész', title: 'A vegyész' ,description:''},
    { id: 'Ady Endre Összes Versei', title: 'Összes Versei' ,description:''},
    { id: 'Goriot apó', title: 'Goriot apó' ,description:''},
    { id: 'Stanley kincse', title: 'Stanley kincse' ,description:''},
    { id: 'Arany János válogatott versei', title: 'Arany János válogatott versei' ,description:''},
    { id: 'Az utolsó boszorkány', title: 'Az utolsó boszorkány' },
    { id: 'Veszélyes Gazdaság', title: 'Veszélyes Gazdaság' ,description:''},
    { id: 'Agykontroll', title: 'Agykontroll' ,description:''},
    { id: 'Magyar szótár', title: 'Szótár' ,description:''},
    { id: 'Görög drámák', title: 'Görög drámák' ,description:''},
    { id: 'Huhogók', title: 'Huhogók' ,description:''},
    { id: 'Az elvarázsolt lélek', title: 'Az elvarázsolt lélek' ,description:''},
    { id: 'Éjjeli Őrjárat', title: 'Éjjeli Őrjárat' },
    { id: 'Tizenkilencen', title: 'Tizenkilencen' },
 
  ];

  const [filteredBooks, setFilteredBooks] = useState(initialBooks);
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const searchTerm = searchParams.get('search');

    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      const filtered = initialBooks.filter(book =>
        book.title.toLowerCase().includes(term)
      );
      setFilteredBooks(filtered);
    } else {
      setFilteredBooks(initialBooks);
    }
  }, [location]); // Függőségként a location-t adjuk meg, hogy reagáljunk az URL változásaira

  return (
    <main className="page-content">
      {filteredBooks.map(book => (
        <div key={book.id} className="card">
          <div className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy">{book.description}</p>
            <button className="btn">Info</button>
          </div>
        </div>
      ))}
    </main>
  );
};

export default Books;
