import React, { useState } from 'react';
import './Books.css';

// Tegyük fel, hogy ezek a könyvek a kezdeti állapotod
const initialBooks = [
  { id: 1, title: 'Naruto Shippuden', description: 'Egy ninja története...' },
  { id: 2, title: 'Tokyo Ghoul', description: 'Egy fiatal fiú, aki ghoul-lá válik...' },
  // További könyvek...
];

const Books = () => {
  const [showModal, setShowModal] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);

  const handleInfoClick = (bookId) => {
    const bookDetails = initialBooks.find(book => book.id === bookId);
    setSelectedBook(bookDetails);
    setShowModal(true);
  };

  return (
    <main className="page-content">
      {initialBooks.map((book) => (
        <div key={book.id} className="card">
          <div id={book.id} className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy">{book.description}</p>
            <button className="btn" onClick={() => handleInfoClick(book.id)}>Info</button>
          </div>
        </div>
      ))}
      {showModal && selectedBook && (
        <div className="modal">
          <div className="modal-content">
            <h2>{selectedBook.title}</h2>
            <p>{selectedBook.description}</p>
            <button className="btn" onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}
    </main>
  );
};

export default Books;
