import React, { useState } from 'react';
import './Books.css';

const Books = () => {
  const [selectedBook, setSelectedBook] = useState(null);

  const books = [
    { id: '1', title: 'Naruto Shippuden', description: 'Egy ninja története...' },
    { id: '2', title: 'Tokyo Ghoul', description: 'Egy fiatal fiú, aki ghoul-lá válik...' },
    { id: '3', title: 'Csokonai Vitéz Mihály' },
    { id: '4', title: 'Sebzett madár' },
    { id: '5', title: 'Toldi-trilógia' },
    { id: '6', title: 'Jónás Könyve' },
    { id: '7', title: 'A vegyész' },
    { id: '8', title: 'Összes Versei' },
    { id: '9', title: 'Goriot apó' },
    { id: '10', title: 'Stanley kincse' },
    { id: '11', title: 'Arany János válogatott versei' },
    { id: '12', title: 'Az utolsó boszorkány' },
    { id: '13', title: 'Veszélyes Gazdaság' },
    { id: '14', title: 'Agykontroll' },
    { id: '15', title: 'Szótár' },
    { id: '16', title: 'Görög drámák' },
    { id: '17', title: 'Huhogók' },
    { id: '18', title: 'Az elvarázsolt lélek' },
    { id: '19', title: 'Éjjeli Őrjárat' },
    { id: '20', title: 'Tizenkilencen' },
    // További könyvek...
  ];

  const handleOutsideClick = (e) => {
    if (e.target.className === 'modal') {
      setSelectedBook(null);
    }
  };

  return (
    <main className="page-content">
      {books.map((book) => (
        <div key={book.id} className="card">
          <div id={book.id} className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy">{book.description}</p>
            <button className="btn" onClick={() => handleInfoClick(book.id)}>Info</button>
          </div>
        </div>
      ))}
      {selectedBook && (
        <div className="modal">
          <div className="modal-content">
            <h2>{selectedBook.title}</h2>
            <p>{selectedBook.description}</p>
            <button className="btn" onClick={() => setSelectedBook(null)}>Close</button>
          </div>
        </div>
      )}
    </main>
  );
};

export default Books;
