import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Books.css';

const Books = () => {
  const initialBooks = [
    // Könyvek listája...
  ];

  const [filteredBooks, setFilteredBooks] = useState(initialBooks);

  // A useLocation hook használata a keresési feltételek URL-ből való kiolvasására
  const location = useLocation();

  useEffect(() => {
    // A keresési feltétel kiolvasása a URL-ből
    const searchParams = new URLSearchParams(location.search);
    const searchTerm = searchParams.get('search') || '';

    const filtered = initialBooks.filter(book =>
      book.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBooks(filtered);
  }, [location]); // A függőség a location, hogy a keresési feltételek változásakor újra fusson

  return (
    <main className="page-content">
      {filteredBooks.map(book => (
        <div key={book.id} className="card">
          {/* A könyvcímekhez hozzáadott Link komponens a könyv részletes oldalára navigáláshoz */}
          <Link to={`/book/${book.id}`} className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy"></p>
            <button className="btn">Info</button>
          </Link>
        </div>
      ))}
    </main>
  );
};

export default Books;
