import React, { useState } from 'react';
import './UserPage.css'; // Feltételezzük, hogy a CSS fájl már létre lett hozva

const UserPage = ({ onLogout, onUpdateProfile, onFavouriteToggle }) => {
  const [profileImage, setProfileImage] = useState('');
  const [favouriteBooks, setFavouriteBooks] = useState([]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setProfileImage(reader.result);
      onUpdateProfile(reader.result); // Feltételezve, hogy az onUpdateProfile kezeli a profil kép frissítését
    };
    reader.readAsDataURL(file);
  };

  const toggleFavourite = (bookId) => {
    const isFavourite = favouriteBooks.includes(bookId);
    if (isFavourite) {
      setFavouriteBooks(favouriteBooks.filter(id => id !== bookId));
    } else {
      setFavouriteBooks([...favouriteBooks, bookId]);
    }
    onFavouriteToggle(bookId); // Feltételezve, hogy az onFavouriteToggle kezeli a kedvencek listájának frissítését
  };

  return (
    <div className="user-page">
      <h1>Felhasználói Profil</h1>
      <button onClick={onLogout}>Kijelentkezés</button>
      <div>
        <label>Profilkép: </label>
        <input type="file" onChange={handleImageChange} />
        {profileImage && <img src={profileImage} alt="Profilkép" />}
      </div>
      <div>
        <h2>Kedvenc könyveim</h2>
        {favouriteBooks.map(bookId => (
          <div key={bookId}>
            <span>Könyv címe</span> {/* A valós alkalmazásban itt kellene megjeleníteni a könyv címét */}
            <button onClick={() => toggleFavourite(bookId)}>Eltávolítás</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserPage;
