import React, { useState, useEffect } from 'react';
import { Link,useNavigate } from 'react-router-dom';
import './Navbar.css';

import LanguageSelector from './LanguageSelector';


const Navbar = () => {
  const [isLogo1, setIsLogo1] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);
  const navigate = useNavigate(); // Hozzáadva

  const toggleLogo = () => {
    setIsLogo1((prevIsLogo) => !prevIsLogo);
  };

  const logoPath = isLogo1 ? 'icon10.png' : 'icon3.png';

  const handleSearch = () => {
    console.log(`Search term: ${searchTerm}`);
    setSearchHistory((prevHistory) => [...prevHistory, searchTerm]);

    // Itt történik a navigálás a /books útvonalra, keresési feltételekkel
    navigate(`/books?search=${encodeURIComponent(searchTerm)}`);
    setSearchTerm('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault(); // Megakadályozza az űrlap alapértelmezett beküldési műveletét
      handleSearch();
    }
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      toggleLogo();
    }, 5000);

    return () => clearInterval(intervalId);
  }, []);

  

  return (
    <nav className="navbar">
        {isLogo1 ? (
          <Link to="https://github.com/LibraKando/Project">
            <img src={logoPath} height={50} alt='' />
          </Link>
        ) : (
          <Link to="https://www.kkszki.hu/">
            <img src={logoPath} height={50} alt='' />
          </Link>
        )}
      <div className="navbar-right">
        <ul className="navbar-menu">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/books">Books</Link></li>
          <li><Link to="/login">Login</Link></li>
        </ul>
        <div className="navbar-actions">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search books..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={handleKeyPress}
              list="psychology_theories_list"
            />
            {searchHistory.length > 0 && (
              <datalist id="psychology_theories_list">
                {searchHistory.map((item, index) => (
                  <option key={index} value={item} />
                ))}
              </datalist>
            )}
            
          </div>
          
        </div>
        <div className="frame">
            <button className="search-button" onClick={handleSearch}>Search </button>
        
      </div>
      <LanguageSelector/>
      </div>
    </nav>
  );
};

export default Navbar;
