import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from './';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('URL_BEJELENTKEZESI_API', { email, password });
      if (response.status === 200) {
        login(response.data); // Feltételezve, hogy a válasz tartalmazza a felhasználói adatokat
        navigate('/user');
      }
    } catch (error) {
      console.error('Bejelentkezés sikertelen', error);
    }
  };

  return (
    <div className="login-root">
      {/* A bejelentkezési űrlap kódja */}
    </div>
  );
};

export default Login;
