import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Books.css';

const Books = ({ searchTerm }) => {
  // Példa könyv adatok
  const initialBooks = [
    { id: 'Naruto Shippuden', title: 'Naruto Shippuden' },
    { id: 'Tokyo Ghoul', title: 'Tokyo Ghoul' },
    { id: 'Dorottya', title: 'Csokonai Vitéz Mihály' },
    { id: 'Sebzett madár', title: 'Sebzett madár' },
    { id: 'Toldi-trilógia', title: 'Toldi-trilógia' },
    { id: 'Jónás Könyve', title: 'Jónás Könyve' },
    { id: 'A vegyész', title: 'A vegyész' },
    { id: 'Ady Endre Összes Versei', title: 'Összes Versei' },
    { id: 'Goriot apó', title: 'Goriot apó' },
    { id: 'Stanley kincse', title: 'Stanley kincse' },
    { id: 'Arany János válogatott versei', title: 'Arany János válogatott versei' },
    { id: 'Az utolsó boszorkány', title: 'Az utolsó boszorkány' },
    { id: 'Veszélyes Gazdaság', title: 'Veszélyes Gazdaság' },
    { id: 'Agykontroll', title: 'Agykontroll' },
    { id: 'Magyar szótár', title: 'Szótár' },
    { id: 'Görög drámák', title: 'Görög drámák' },
    { id: 'Huhogók', title: 'Huhogók' },
    { id: 'Az elvarázsolt lélek', title: 'Az elvarázsolt lélek' },
    { id: 'Éjjeli Őrjárat', title: 'Éjjeli Őrjárat' },
    { id: 'Tizenkilencen', title: 'Tizenkilencen' },
    // További könyvek
  ];

  // Szűrt könyvek állapotának inicializálása
  const [filteredBooks, setFilteredBooks] = useState(initialBooks);

  // Szűrési logika az searchTerm alapján minden alkalommal, amikor a searchTerm megváltozik
  useEffect(() => {
    if (searchTerm) {
      const filtered = initialBooks.filter(book =>
        book.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredBooks(filtered);
    } else {
      setFilteredBooks(initialBooks); // Ha nincs keresési szöveg, minden könyvet megjelenítünk
    }
  }, [searchTerm]);

  return (
    <main className="page-content">
      {filteredBooks.map(book => (
        <div key={book.id} className="card">
          {/* Link komponens hozzáadva a könyv címéhez */}
          <Link to={`/book/${book.id}`} className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy"></p>
            <button className="btn">Info</button>
          </Link>
        </div>
      ))}
    </main>
  );
};

export default Books;
