import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordVisible, setPasswordVisible] = useState(false); 
  const [userList, setUserList] = useState([]);
  const [isLoggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const axiosInterceptor = axios.interceptors.request.use((config) => {
      const token = localStorage.getItem('authToken');
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
      return config;
    }, (error) => Promise.reject(error));

    return () => axios.interceptors.request.eject(axiosInterceptor);
  }, []);

  const login = async (e) => {
    e.preventDefault();
    try {
      const loginResponse = await axios.post('https://api.example.com/login', { email, password });
      if (loginResponse.status === 200 && loginResponse.data.token) {
        localStorage.setItem('authToken', loginResponse.data.token);
        setLoggedIn(true);
      } else {
        console.error('Bejelentkezés sikertelen');
      }
    } catch (error) {
      console.error('Hiba történt a bejelentkezés során:', error);
    }
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    setLoggedIn(false);
  };

  useEffect(() => {
    if (isLoggedIn) {
      axios.get('https://api.example.com/users')
        .then((response) => setUserList(response.data))
        .catch((error) => console.error('Felhasználók lekérése sikertelen', error));
    }
  }, [isLoggedIn]);

  return (
    <div className="login-root">
    {!isLoggedIn && (
      <div className="box-root padding-top--24 flex-flex flex-direction--column" style={{ flexGrow: 1, zIndex: 9 }}>
            <div className="box-root padding-top--48 padding-bottom--24 flex-flex flex-justifyContent--center">
          <h1>LibraryLogin</h1>
        </div>
        <div className="formbg-outer">
          <div className="formbg">
            <div className="formbg-inner padding-horizontal--48">
              <h1></h1>
              <span className="padding-bottom--15">Sign in to your account</span>
              <form id="stripe-login" onSubmit={login}>
                <div className="field padding-bottom--24">
                  <label htmlFor="email">Email</label>
                  <input type="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                </div>
                <div className="field padding-bottom--24">
                  <label htmlFor="password">Password</label>
                  <input
                    type={passwordVisible ? "text" : "password"} // Jelszó mező típusának kezelése
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <div className="reset-pass">
                    <a href="/">Forgot your password?</a>
                  </div>
                  <div className="field-checkbox padding-top--8">
                    <label>
                      <input
                        type="checkbox"
                        checked={passwordVisible}
                        onChange={() => setPasswordVisible(!passwordVisible)} // Jelszó láthatóságának váltása
                      /> Show Password
                    </label>
                  </div>
                </div>
                <div className="field padding-bottom--24">
                  <input type="submit" name="submit" value="Sign in" />
                </div>
              </form>
            </div>
          </div>
          <div className="footer-linkk padding-top--24">
            <span>Don't have an account? <a href="/">Sign up</a></span>
          </div>
        </div>
      </div>
    )}

    {/* User list and Logout button */}
    {isLoggedIn && (
      <div>
        <button onClick={logout} style={{ margin: '20px 0' }}>Logout</button>
        <h2>Felhasználók listája</h2>
        {userList.map((user) => (
          <div key={user.id}>
            <strong>{user.name}</strong><br/>
            Email: {user.email}
          </div>
        ))}
      </div>
    )}
  </div>
);
};

export default Login;
