import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './Books.css';

const Books = () => {
  // A kezdeti könyv lista és a szűrt könyvek állapota
  const initialBooks = [
    // Könyvek listája...
  ];

  const [filteredBooks, setFilteredBooks] = useState(initialBooks);
  const [selectedBook, setSelectedBook] = useState(null); // Új állapot a kiválasztott könyv részleteinek tárolására
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const searchTerm = searchParams.get('search');
  
    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      const filtered = initialBooks.filter(book =>
        book.title.toLowerCase().includes(term)
      );
      setFilteredBooks(filtered);
    } else {
      setFilteredBooks(initialBooks);
    }
  }, [location]);

  // Funkció a kiválasztott könyv beállítására és a modális megnyitására
  const handleBookSelect = (bookId) => {
    const book = initialBooks.find(book => book.id === bookId);
    setSelectedBook(book);
  };

  return (
    <main className="page-content">
      {filteredBooks.map(book => (
        <div key={book.id} className="card">
          <div className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy">{book.description}</p>
            <button className="btn" onClick={() => handleBookSelect(book.id)}>Info</button>
          </div>
        </div>
      ))}
      {selectedBook && (
        <div className="modal">
          <div className="modal-content">
            <h2>{selectedBook.title}</h2>
            <p>{selectedBook.description}</p>
            <button onClick={() => setSelectedBook(null)}>Close</button>
          </div>
        </div>
      )}
    </main>
  );
};

export default Books;
