import React, { useState, useEffect } from 'react';
import './Books.css';
import Modal from './Modal';
import Pagination from './Pagination';

const Books = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(8);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);

  const books = [


    

  ];

  useEffect(() => {
    if (searchTerm) {
      const searchResults = books.filter(book =>
        book.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredBooks(searchResults);
    } else {
      setFilteredBooks(books);
    }
  }, [searchTerm, books]); // Figyeli a searchTerm és books változásait

  const handleInfoClick = bookId => {
    const book = filteredBooks.find(book => book.id === bookId); // Keresés a szűrt könyvek között
    setSelectedBook(book);
    document.body.style.overflow = 'hidden';
  };

  const handleCloseModal = () => {
    setSelectedBook(null);
    document.body.style.overflow = 'auto';
  };

  // Számítás az aktuális oldalon megjelenítendő könyvekről a szűrt listából
  const indexOfLastBook = currentPage * itemsPerPage;
  const indexOfFirstBook = indexOfLastBook - itemsPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <main className="page-content">
      {currentBooks.map(book => (
        <div key={book.id} className="card">
          <div className="content">
            <h2 className="title">{book.title}</h2>
            <p className="copy">{book.description || "Nincs elérhető leírás."}</p>
            <button className="btn" onClick={() => handleInfoClick(book.id)}>Info</button>
          </div>
        </div>
      ))}
      <Pagination
        itemsPerPage={itemsPerPage}
        totalItems={filteredBooks.length} // Frissítve, hogy a szűrt könyvek számát használja
        paginate={paginate}
      />
      {selectedBook && <Modal book={selectedBook} onClose={handleCloseModal} />}
    </main>
  );
};

export default Books;




