const Navbar = () => {
  const [isLogo1, setIsLogo1] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();
  const [searchHistory, setSearchHistory] = useState([]);

  const toggleLogo = () => {
    setIsLogo1(prev => !prev);
  };

  const handleSearch = () => {
    if (searchTerm !== '') {
      const updatedHistory = new Set([searchTerm, ...searchHistory]);
      setSearchHistory([...updatedHistory]);
      navigate(`/books?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  useEffect(() => {
    const intervalId = setInterval(toggleLogo, 5000);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <nav className="navbar">
      {/* Navbar tartalma */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for books..."
          className="search-input"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          onKeyPress={handleKeyPress}
          list="book_search_history"
        />
        <button className="search-button" onClick={handleSearch}>Search</button>
        {searchHistory.length > 0 && (
          <datalist id="book_search_history">
            {searchHistory.map((item, index) => (
              <option key={index} value={item} />
            ))}
          </datalist>
        )}
      </div>
      <LanguageSelector />
    </nav>
  );
};
